mapper.select:
	select id,
   	partyId,
   	orderNo,
   	acctName,
   	acct,
   	acctType,
   	amt,
   	feeRate,
   	srvFee,
   	acAmt,
   	status,
   	crtTime,
   	updTime,
   	respData,
   	memo
   from ha_withdraw_record
   where 1=1

   	and partyId=#{cond.partyId}


   	and acctType=#{cond.acctType}


   	and acctName=#{cond.acctName}


   	and acct=#{cond.acct}


   	and status=#{cond.status}


   	and startDate >= #{cond.startDate}


   	and endDate <= #{cond.endDate}

   limit #{offset},#{limit}

select id, partyId, orderNo, acctName, acct, acctType, amt, feeRate, srvFee, acAmt, status, crtTime, updTime, respData, memo from ha_withdraw_record hwr where hwr.partyId = #{partyId}
AND hwr.crtTime >= #{startTime} AND hwr.crtTime < #{endTime}
order by ${sortField} ${sortDirection} , crtTime DESC limit #{begin},#{end}

 	select IFNULL(sum(IFNULL(hwr.amt,0)),0)
    from ha_withdraw_record hwr 
    where hwr.partyId = #{partyId}

		AND hwr.crtTime >= #{startTime}
		AND hwr.crtTime < #{endTime}

	order by ${sortField} ${sortDirection} , crtTime DESC
	limit #{begin},#{end}
